mapper.select:
SELECT COUNT(*)
	FROM T_SGJ_CART
		WHERE USER_ID = #{userId}
			AND PRDCT_NO = #{prdctNo}
			AND PRDCT_DETAIL_NO = #{prdctDetailNo}

mapper.update:
UPDATE T_SGJ_CART
	SET CART_CNT = CART_CNT + 1
 	WHERE USER_ID = #{userId}
		AND PRDCT_NO = #{prdctNo}
		AND PRDCT_DETAIL_NO = #{prdctDetailNo}
